<?phpinclude 'models/chat.config.php';$db->query('SELECT * FROM messages ORDER BY (id) ASC');$data = $db->GET();foreach($data as $key => $value) {		$color = htmlentities($value['color']);	$user = htmlentities($value['username']);	$msg = htmlentities($value['message']);	echo "<li id='msg_row'><b id='u_name_chat' style='color: ".$color.";'>".$user."</b>: ".$msg."</li>";}?>